package io.vteial.seetu.modelimport groovy.transform.Canonicalimport groovyx.gaelyk.datastore.Entityimport groovyx.gaelyk.datastore.Ignoreimport groovyx.gaelyk.datastore.Key@Entity(unindexed=false)@Canonicalclass ItemSubscriber implements Serializable {	static final String ID_KEY = "itemSubscriberId"	@Key	long id	long itemId	@Ignore	Item item	long customerId	@Ignore	Customer customer	String createBy	String updateBy	Date createTime	Date updateTime	void preUpdate(String updateBy) {		this.updateBy = updateBy		this.updateTime = new Date()	}	void prePersist(String createAndUpdateBy) {		this.createBy = createAndUpdateBy		this.updateBy = createAndUpdateBy		Date now = new Date()		this.createTime = now;		this.updateTime = now;	}}
